mapper.select:
SELECT W_NO, EMP_NO, TO_DATE(TO_CHAR(W_DATE, 'YYMMDD'), 'YY/MM/DD') W_DATE, CASE WHEN START_TIME = NULL THEN NULL ELSE SUBSTR(TO_TIMESTAMP(START_TIME, 'yyyy/MM/dd hh24:MI:ss'), 10, 8) END START_TIME, CASE WHEN END_TIME = NULL THEN NULL ELSE SUBSTR(TO_TIMESTAMP(END_TIME, 'yyyy/MM/dd hh24:MI:ss'), 10, 8) END END_TIME, TRUNC((TO_DATE(END_TIME, 'YYYY-MM-DD HH24:MI:SS') - TO_DATE(START_TIME, 'YYYY-MM-DD HH24:MI:SS'))*24) || 'h ' || TRUNC((TO_DATE(END_TIME, 'YYYY-MM-DD HH24:MI:SS') - TO_DATE(START_TIME, 'YYYY-MM-DD HH24:MI:SS'))*24*60 - TRUNC((TO_DATE(END_TIME, 'YYYY-MM-DD HH24:MI:SS') - TO_DATE(START_TIME, 'YYYY-MM-DD HH24:MI:SS'))*24)*60) || 'm ' || TRUNC((TO_DATE(END_TIME, 'YYYY-MM-DD HH24:MI:SS') - TO_DATE(START_TIME, 'YYYY-MM-DD HH24:MI:SS'))*24*60*60 - TRUNC((TO_DATE(END_TIME, 'YYYY-MM-DD HH24:MI:SS') - TO_DATE(START_TIME, 'YYYY-MM-DD HH24:MI:SS'))*24*60)*60) || 's' TOTAL_TIME FROM WORK WHERE EMP_NO = #{empNo}
UNION
SELECT NULL, NULL, YMD W_DATE, NULL, NULL, NULL FROM ( SELECT NEXT_DAY((TRUNC(TO_DATE(#{strDate}, 'yy/MM/dd'),'MONTH')-6),'월')+(LEVEL - 1) YMD FROM DUAL CONNECT BY NEXT_DAY((TRUNC(TO_DATE(#{strDate}, 'yy/MM/dd'),'MONTH')-6),'월')+(LEVEL-1) <= NEXT_DAY(LAST_DAY(TO_DATE(#{strDate}, 'yy/MM/dd')), '일') ) WHERE YMD NOT IN ( SELECT TO_CHAR(W_DATE,'YY/MM/DD') FROM WORK WHERE EMP_NO = #{empNo} ) ORDER BY W_DATE

mapper.update:
UPDATE WORK
SET END_TIME=TO_CHAR(CURRENT_TIMESTAMP, 'yyyy-MM-dd HH24:MI:SS')
WHERE W_NO=#{wNo}
